PART.SQL:

 select 
 a.Id
 ,a.DeliveryaddressID
 ,a.Customertype
 ,a.Name
 ,a.Birthday
 ,a.Address
 ,a.Tel
 ,a.Email
 ,a.Sex
 ,a.Createtime
 ,a.Updatetime
 ,a.Isdelete
 ,b.Type
 ,b.Address as DefualtAddress
 from Customer a
join Address b on a.Id=b.id
where 1=1
{%DeliveryaddressID_Off%} and {%DeliveryaddressID%}
{%Customertype_Off%}    and {%Customertype%}
{%Name_Off%}            and {%Name%}
{%Birthday_Off%}        and {%Birthday%}
{%Address_Off%}         and {%Address%}
{%Tel_Off%}	            and {%Tel%}
{%Email_Off%}	          and {%Email%}
{%Sex_Off%}	            and {%Sex%}
