PART.SQL:

       declare @newid uniqueidentifier  
       set newid=newid() 
      INSERT INTO [dbo].[OrderMain]
           ([Id]
           ,[Orderno]
           ,[CreateuserID]
           ,[CompanyID]
           ,[Totalmoney]
           ,[Paymentamount]
           ,[Paidamount]
           ,[Createtime]
           ,[Updatetime]
           ,[Ordertime]
           ,[IsDelete])
     VALUES
           ( newid
           ,{%Orderno%}
           ,{%CreateuserID%}
           ,{%CompanyID%}
           ,{%Totalmoney%}
           ,{%Paymentamount%}
           ,{%Paidamount%}
           ,GETDATE()
           ,GETDATE()
           ,0)
   INSERT INTO [dbo].[OrderDetail]
           ([Id]
           ,[OrderMainID]
           ,[Goodsname]
           ,[Unit]
           ,[Num]
           ,[Destination]
           ,[Totalmoney]
           ,[Paymentamount]
           ,[Paidamount]
           ,[Createetime])
     VALUES
        (newid() 
		   ,newid
		   ,{%Goodsname%}
		   ,{%Unit%}
		   ,{%Num%}
		   ,{%Destination%}
		   ,{%Totalmoney%}
		   ,{%Paymentamount%}
		   ,{%Paidamount%}
		   ,{%Paidamount%}
		   ,GETDATE()
			)


UPDATE [dbo].[OrderMain]
   SET		
	Updatetime=getdate()
	{%CreateuserID_Off%}  ,CreateuserID={%CreateuserID%}
	{%CompanyID_Off%}  ,CompanyID={%CompanyID%}
	{%Totalmoney_Off%}  ,Totalmoney={%Totalmoney%}
	{%Paymentamount_Off%}  ,Paymentamount={%Paymentamount%}
	{%Paidamount_Off%}  ,Paidamount={%Paidamount%}
	{%CreateuserID_Off%}  ,CreateuserID={%CreateuserID%}
 WHERE Id={%Id%}
   UPDATE [dbo].[OrderDetail]
   SET 
	    Goodsname={%Goodsname%}
	   {%[Unit_Off%}  ,Unit={%Unit%}
	   {%Num_Off%}  ,Num={%Num%}
	   {%Destination_Off%}  ,Destination={%Destination%}
	   {%Totalmoney_Off%}  ,Totalmoney={%Totalmoneydetail%}
	   {%Paymentamount_Off%}  ,Paymentamount={%Paymentamountdetail%}
	   {%Paidamount_Off%}  ,Paidamount={%Paidamountdetail%}
 WHERE Id={%Id%}
